package co.com.bryan.basic;

public class Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//casting:  conversion tipo de dato
		
		/*	
		 * para realizar la conversion debemos especificar entre
		 * parentesis () el tipo de dato a realizar la conversion
		*/
		byte numeroA = 2;
		int numero = 4;
		
		numeroA = (byte)numero;
		System.out.println(numeroA);
	}

}
