package co.com.bryan.basic;

public class CondicionSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * para selecciones multiples
		 */
		
		int option = 3;
		
		switch(option){
			case 1:
				System.out.println("Minutos");
				break;
			case 2:
				System.out.println("recargas");
				break;
			case 3:
				System.out.println("Datos");
				break;
			
			default:
				break;
		}
	}

}
